// Extract fields
let ts = parseFloat(msg.payload.timestamp);
let id = String(msg.payload.CANID).toLowerCase(); // normalize to string
let data = String(msg.payload.CANDATA);

let result = {};

// Convert hex string to bytes
let bytes = [];
for (let i = 0; i < data.length; i += 2) {
    bytes.push(parseInt(data.substr(i, 2), 16));
}

if (id === "101") {
    // Cylinder Pressure
    let raw = ((bytes[3] << 2) | (bytes[4] >> 6)) & 0x3FF;
    let value = raw * 0.02 + 0;

    result = {
        CANID: id,
        Label: "CylinderPressure",
        Value: value,
        Unit: "Mpa",
        Timestamp: ts
    };
}
else if (id === "d7") {
    // Vehicle Speed
    let raw = (bytes[2] << 8) | bytes[3];
    let value = raw * 0.01 + 0;

    result = {
        CANID: id,
        Label: "VehicleSpeed",
        Value: value,
        Unit: "km/h",
        Timestamp: ts
    };
}

msg.payload = result;
return msg;
